mapper.select:
select addr, case when order_type = '1' then '快递派件' when order_type = '2' then '用户暂存' when order_type = '3' then '用户寄件' else '其他' end as order_type, express_number, courier_send_name, courier_phone, send_time, getter_phone, getter_time, send_total_money, state, device_id, `code`, create_date
from ( SELECT concat( d.device_remark,' ', c.total_door_id, '柜门' ) addr, o.send_device_id as device_id, c.total_door_id, o.order_type, o.express_number, o.create_date, if(o.courier_send_name is null, o.sender_user_name, o.courier_send_name) as courier_send_name, if(u.phone is null, o.sender_phone, u.phone) as courier_phone, if(o.user_id is null , o.sender_time, o.courier_send_time) send_time, o.getter_phone, o.getter_time, o.send_total_money, o.state, o.status, cd.`code` FROM zhinenggui_order o JOIN zhinenggui_device d ON o.send_device_id = d.device_id JOIN js_sys_user su on d.by_user = su.user_code
and su.user_code =#{userCode}
JOIN zhinenggui_cabinets c ON o.send_door_id = c.id LEFT JOIN zhinenggui_user u on o.courier_send_id = u.id
JOIN zhinenggui_code cd on cd.id = o.code_id and cd.`code`= #{code}
left JOIN zhinenggui_code cd on cd.id = o.code_id
) as f where f.status='0'
and f.code = #{code}
and locate(#{expressNumber}, f.express_number) > 0
and locate(#{courierSendName}, f.courier_send_name) > 0
and locate(#{getterPhone}, f.getter_phone) > 0
and f.state = #{state}
and f.order_type = #{orderType}
and locate(#{deviceId}, f.device_id) > 0
and f.total_door_id = #{totalDoorId}
ORDER BY create_date DESC

SELECT
	  c.*, o.id orderId, o.courier_send_time courierSendTime, o.sender_time senderTime, o.send_total_money sendTotalMoney, co.`type`,
		  IFNULL(p.money,0) money, IFNULL(p.over_time,0) overTime, IFNULL(p.over_type, '0') overType, IFNULL(p.over_money, 0) overMoney, IFNULL(p.over_unit, 0) overUnit,
		  IFNULL(p.money1,0) money1, IFNULL(p.over_time1,0) overTime1, IFNULL(p.over_type1, '0') overType1, IFNULL(p.over_money1, 0) overMoney1, IFNULL(p.over_unit1, 0) overUnit1
FROM

	zhinenggui_code co
	JOIN zhinenggui_order o ON o.code_id = co.id
	AND co.code = #{validCode}
	AND ( o.state = '400' OR o.state = '500' )
	and o.send_device_id = #{deviceId}
	JOIN zhinenggui_cabinets c ON o.send_door_id = c.id
	AND c.device_id = #{deviceId}
	LEFT JOIN zhinenggui_door_price p on c.id = p.door_id;
